import React, {useState, useEffect} from 'react';
import { useHistory, NavLink} from 'react-router-dom';
import "./Home.css";
import axios from "axios";

const Home = () => {

  const history = useHistory();
  const [userData, setUserData] = useState();

  var [user, setUser] = useState({
    brand : "", model : "", oldprice : "", yearsold : "", ownership : "", location : "", kmsdriven : "",
    imageuploadfront : "", imageuploadback : "", imageuploadleftrear : "", imageuploadrightrear : "",
    audioupload : "", videoupload : ""
  });

  let name, value;

  const handleInputs = (e) => {
    name = e.target.name;
    value = e.target.value;
    setUser({...user, [name] : value});
  }

  const uploadfile = (filename, file, i) => {
    if(i === 0) file.mv("C:/Users/HarshGupta/Desktop/Image_user/Front_view/" + filename, function(err){
      if(err) console.log(err); else console.log("reached in front");
    });
    else if(i === 1) file.mv("C:/Users/HarshGupta/Desktop/Image_user/Rear_view/" + filename, function(err){
      if(err) console.log(err); else console.log("reached in back");
    });
    else if(i === 2) file.mv("C:/Users/HarshGupta/Desktop/Image_user/LSide _view/" + filename, function(err){
      if(err) console.log(err); else console.log("reached in left side");
    });
    else file.mv("C:/Users/HarshGupta/Desktop/Image_user/RSide_view/" + filename, function(err){
      if(err) console.log(err); else console.log("reached in right side");
    });
  }

  const evaluate = (e) => {
    const inpFile = document.getElementById("inpFile");
    const btnUpload = document.getElementById("btnUpload");
    for(var i = 0; i < inpFile.files.length; i ++){
      var file= inpFile.files.file;
      var filename = file.name;
      uploadfile(filename, file, i);
    }
  }

  return (
    <div>
      <div className = "outer-form-div-home">
        <form>
          <div>
            <input type = "text" name = "brand" id = "brand" autoComplete = "on" placeholder='Brand'
            value = {user.brand} onChange = {handleInputs}/>
          </div>
          <div>
            <input type = "text" name = "model" id = "model" autoComplete = "on" placeholder='Model'
            value = {user.model} onChange = {handleInputs}/>
          </div>
          <div>
            <input type = "number" name = "oldprice" id = "oldprice" autoComplete = "on" placeholder='Old Price'
            value = {user.oldprice} onChange = {handleInputs}/>
          </div>
          <div>
            <input type = "number" name = "yearsold" id = "yearold" autoComplete = "on" placeholder='Years Old'
            value = {user.yearsold} onChange = {handleInputs}/>
          </div>
          <div>
            <input type = "number" name = "ownership" id = "ownership" autoComplete = "on" placeholder='OwnerShip'
            value = {user.ownership} onChange = {handleInputs}/>
          </div>
          <div>
            <input type = "text" name = "location" id = "location" autoComplete = "on" placeholder='Location'
            value = {user.location} onChange = {handleInputs}/>
          </div>
          <div>
            <input type = "number" name = "kmsdriven" id = "kmsderiven" autoComplete = "on" placeholder='Kms Driven'
            value = {user.kmsdriven} onChange = {handleInputs}/>
          </div>
          <div className='upload-details'>
            <input type="file" name = "file" id = "inpFile" multiple/>
            <button id = "btnUpload" onClick={evaluate}>Upload Files</button>
          </div>
        </form>
      </div>
    </div>
  )
}

export default Home
