import React, {useState, useEffect} from 'react';
import { useHistory, NavLink} from 'react-router-dom';
import "./Home.css";

const Home = () => {

  const history = useHistory();
  const [userData, setUserData] = useState();

  const [user, setUser] = useState({
    brand : "", model : "", oldprice : "", yearsold : "", ownership : "", location : "", kmsdriven : "",
    imageupload : "", audioupload : "", videoupload : ""
  });

  let name, value;

  const handleInputs = (e) => {
    name = e.target.name;
    value = e.target.value;
    setUser({...user, [name] : value});
    console.log(user);
  }

  const PostData = (e) => {
    e.preventDefault();
    const {brand, model, oldprice, yearsold, ownership, location, kmsdriven, imageupload, audioupload, videoupload} = user;
    ima=document.getElementById('FileUpload1').value
    alert(fake_path.split("\\").pop())
  }

  const callAboutPage = async () => {
    try{
      const res = await fetch("/about", {
        method : "GET",
        headers : {
          Accept : "application/json",
          "Content-Type" : "application/json"
        },
        credentials : "include"
      });

      const data = await res.json();
      setUserData(data);

      if(!res.status === 200){
        const error = new Error(res.error);
        throw error;
      }

    }catch(err){
      history.push("/login");
    }
  }

  useEffect(() => {
    callAboutPage();
  }, []);

  return (
    <div>
      <div className = "outer-form-div-home">
        <form>
          <div>
            <input type = "text" name = "brand" id = "brand" autoComplete = "on" placeholder='Brand'
            value = {user.brand} onChange = {handleInputs}/>
          </div>
          <div>
            <input type = "text" name = "model" id = "model" autoComplete = "on" placeholder='Model'
            value = {user.model} onChange = {handleInputs}/>
          </div>
          <div>
            <input type = "number" name = "oldprice" id = "oldprice" autoComplete = "on" placeholder='Old Price'
            value = {user.oldprice} onChange = {handleInputs}/>
          </div>
          <div>
            <input type = "number" name = "yearsold" id = "yearold" autoComplete = "on" placeholder='Years Old'
            value = {user.yearsold} onChange = {handleInputs}/>
          </div>
          <div>
            <input type = "number" name = "ownership" id = "ownership" autoComplete = "on" placeholder='OwnerShip'
            value = {user.ownership} onChange = {handleInputs}/>
          </div>
          <div>
            <input type = "text" name = "location" id = "location" autoComplete = "on" placeholder='Location'
            value = {user.location} onChange = {handleInputs}/>
          </div>
          <div>
            <input type = "number" name = "kmsdriven" id = "kmsderiven" autoComplete = "on" placeholder='Kms Driven'
            value = {user.kmsdriven} onChange = {handleInputs}/>
          </div>
          <div className='upload-details'>
            <div className='inner-css'>
              <p>Upload Image</p>
              <input type="file" id="myFileimage" name="filename" accept='image/jpg, image/png'></input>
            </div>
            <div className='inner-css'>
              <p>Upload Audio</p>
